import React from "react";

export default function Radios(props) {
	const {priorities, todo, updateTodo} = props;
	return (
		<div className="form-group">
			{priorities.map((priority) => {
				return <div className="form-check form-check-inline" key={priority.name}>
					<input
						className="form-check-input"
						type="checkbox"
						name="priority"
						id={priority.name}
						value={priority.name}
						checked={todo.priority === priority.name}
						onChange={(e) => updateTodo({ priority: e.target.value })}
					/>
					<label htmlFor="priority" className="form-check-label">{priority.name}</label>
			</div>
			})}
		</div>
	)
}
