import React from "react";

export default function Radios(props) {
	const {priorities, todo, updateTodo} = props;
	return (
		<div className="priority-button">
			<span className="material-symbols-outlined">
				flag
			</span>
			<div className="form-group priority-form">
				<ul>
					{priorities.map((priority) => {
						return <li className="priority-list-container" key={priority}>
							<span className="material-symbols-outlined">
								flag
							</span>
							<div className="form-check form-check-inline">
								{/*
								<input
									className="form-check-input"
									type="radio"
									name="priority"
									id={priority}
									value={priority}
									checked={todo.priority === priority}
									onChange={(e) => updateTodo({ priority: e.target.value })}
								/>
								*/}
								<span className={`priority-option-text ${priority}`}>
									{priority}
								</span>
							</div>
						</li>
					})}
				</ul>
			</div>
		</div>
	)
}
