import React from "react";

export default function Radios(props) {
	const {priorities, todo, updateTodo} = props;
	const prioritiesList = [];

	function priorityClick(e) {
		updateTodo({priority: e.target.classList[1]});
	}

	(function mapPriorities() {
		let currentPriority = [];
		priorities.map((priority) => {
			if (todo.priority === priority) {
				currentPriority = [
					<span className={`material-symbols-outlined priority-check ${priority}-color`} key={"check"}>
						check
					</span>
				];
			} else {
				currentPriority = [];
			}
			prioritiesList.push(
				<li className={`priority-list-container ${priority}`} key={priority} onClick={(e) => priorityClick(e)}>
					<svg style={{width: "24px", height: "24px"}} viewBox="0 0 24 24" className="priority-svg">
						<path className={`${priority}-color`} d="M14.4,6L14,4H5V21H7V14H12.6L13,16H20V6H14.4Z" />
					</svg>
					{/*
					<input
						className="form-check-input"
						type="radio"
						name="priority"
						id={priority}
						value={priority}
						checked={todo.priority === priority}
						onChange={(e) => updateTodo({ priority: e.target.value })}
					/>
					*/}
					<span className="priority-option-text">
						{priority.charAt(0).toUpperCase() + priority.slice(1)}
					</span>
					{currentPriority}
				</li>
			)
		})
	})();

	function buttonClick() {
		document.getElementsByClassName('priority-form')[0].classList.add('priority-form--show');
	}

	return (
		<div className="priority-button">
			<span className="material-symbols-outlined" onClick={() => buttonClick()}>
				flag
			</span>
			<div className="form-group priority-form">
				<ul>
					{prioritiesList}
				</ul>
			</div>
		</div>
	)
}
