import React, { useState } from "react";
import { useNavigate } from "react-router";
import Colors from "./colorForm";

export default function CreateCategory() {
 const [category, setCategory] = useState({
   name: "",
   color: {
    colorHex: "#78909C",
    colorName: "Regent Gray",
   },
 });
 const navigate = useNavigate();

 // These methods will update the state properties.
 function updateCategory(value) {
   return setCategory((prev) => {
     return { ...prev, ...value };
   });
 }

 // This function will handle the submission.
 async function onSubmit(e) {
   e.preventDefault();

   // When a post request is sent to the create url, we'll add a new category to the database.
   const newPerson = { ...category };

   //fiux
   await fetch("http://localhost:5000/categories/add", {
     method: "POST",
     headers: {
       "Content-Type": "application/json",
     },
     body: JSON.stringify(newPerson),
   })
   .catch(error => {
     window.alert(error);
     return;
   });

   setCategory({ name: "", color: { colorHex: "#78909C", colorName: "Regent Gray"}, });
   navigate("/");
 }

 // This following section will display the form that takes the input from the user.
 return (
	<div className="category-create">
		<div className="category-header">
			<h3>Create New Category</h3>
		</div>
		<form onSubmit={onSubmit}>
		<div className="form-group">
			<label htmlFor="name">Name</label>
			<input
				type="text"
				className="form-control"
				id="name"
				value={category.name}
				onChange={(e) => updateCategory({ name: e.target.value })}
			/>
		</div>
		<div className="form-group">
			<label htmlFor="color">Color</label>
			<button type="button" className="current-color form-control" value={`${category.color.colorHex} ${category.color.colorName}`}
				onClick={(e) => console.log(e.target)}>
				{/*updateCategory({color: {colorName: category.color, colorHex}})*/}
				<span className="button-color" style={{backgroundColor: category.color.colorHex}} />
				{category.color.colorName}
			</button>
		</div>
		<div className="form-group">
			<input
				type="submit"
				value="Add"
				className="btn btn-primary"
			/>
		</div>
		</form>
	</div>
 );
}
