import React, { useState } from "react";
import { useNavigate } from "react-router";

export default function CreateCategory() {
 const [category, setCategory] = useState({
   name: "",
   color: "",
 });
 const navigate = useNavigate();

 // These methods will update the state properties.
 function updateCategory(value) {
   return setCategory((prev) => {
     return { ...prev, ...value };
   });
 }

 // This function will handle the submission.
 async function onSubmit(e) {
   e.preventDefault();

   // When a post request is sent to the create url, we'll add a new category to the database.
   const newPerson = { ...category };

   //fiux
   await fetch("http://localhost:5000/categories/add", {
     method: "POST",
     headers: {
       "Content-Type": "application/json",
     },
     body: JSON.stringify(newPerson),
   })
   .catch(error => {
     window.alert(error);
     return;
   });

   setCategory({ name: "", color: "" });
   navigate("/");
 }

 // This following section will display the form that takes the input from the user.
 return (
   <div>
     <h3>Create New Category</h3>
     <form onSubmit={onSubmit}>
      <div className="form-group">
        <label htmlFor="description">Description</label>
        <input
          type="text"
          className="form-control"
          id="description"
          value={category.description}
          onChange={(e) => updateCategory({ description: e.target.value })}
        />
      </div>
       <div className="form-group">
         <input
           type="submit"
           value="Add"
           className="btn btn-primary"
         />
       </div>
     </form>
   </div>
 );
}
